    @Rule
    public TestWatcher watcher = new TestWatcher() {
        @Override
        protected void failed(Throwable e, Description description) {
            try {
                File f = new File("/home/bence/Documents/kepek/" + description.getMethodName()
                        + LocalDateTime.now().toString().replace(':', '-') + ".png");
                // File f = new File("C:\\kepek\\" + description.getMethodName()
                // + LocalDateTime.now().toString().replace(':', '-') + ".jpg");
                File a = driverFacade.takeScreenshot();

                FileCopyUtils.copy(a, f);
                driverFacade.quit();
            } catch (IOException e1) {
                throw new RuntimeException(e1);

            }

        }

        @Override
        protected void succeeded(Description description) {
            driverFacade.quit();
        }
    };

    @Before
    public void setUp() {
        FirefoxBinary firefoxBinary = new FirefoxBinary();
        firefoxBinary.addCommandLineOptions("--headless");
        FirefoxOptions firefoxOptions = new FirefoxOptions();
        firefoxOptions.setBinary(firefoxBinary);
        driverFacade = new DriverFacade(new FirefoxDriver(firefoxOptions));
    }

    @Test
    public void signUp() {
        driverFacade.get("http://localhost:" + port);
        LoginAndSignUpPageObject loginAndSignUpPageObject = new LoginAndSignUpPageObject(driverFacade);
        AmusementParkPageObject amusementParkPageObject = loginAndSignUpPageObject.signUp("jeni@gmail.com", "password",
                "1995-03-01", "data:image/jpeg;base64,kep");
        amusementParkPageObject.createButtonHidden();
        amusementParkPageObject.spendingMoneyTest(250);
        amusementParkPageObject.uploadMoney(2000);
        int actMoney = 2250;
        amusementParkPageObject.spendingMoneyTest(actMoney);
        amusementParkPageObject.searchButtonClick();
        amusementParkPageObject.search("Bence's park", 20000, 40000, 2000, 5000, 50, 200, 1);
        amusementParkPageObject.tableBodyFirstClick();
        MachinePageObject machinePageObject = amusementParkPageObject.enterPark();
        machinePageObject.createButtonHidden();
        actMoney -= 100;
        amusementParkPageObject.spendingMoneyTest(actMoney);
        machinePageObject.tableNumberOfRows(5);
        machinePageObject.getOnAndOffMachine(1);
        actMoney -= 15;
        amusementParkPageObject.spendingMoneyTest(actMoney);
        machinePageObject.leave();
        amusementParkPageObject.logout();
    }

